DROP TABLE IF EXISTS `onethink_baidu_user`;CREATE TABLE `onethink_baidu_user` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',  `uid` int(10) NOT NULL DEFAULT '0',  `token` varchar(200) DEFAULT NULL,  `channel_id` varchar(200) DEFAULT NULL,  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '类型（1：Android，2：iOS）',  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '状态（-1：禁用，1：正常）',  `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='百度用户表';DROP TABLE IF EXISTS `onethink_baidu_msg`;CREATE TABLE `onethink_baidu_msg` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',  `uid` int(10) NOT NULL DEFAULT '0',  `msg` varchar(255) DEFAULT NULL,  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '类型（1：系统，2：个人）',  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '状态（-1：禁用，1：正常）',  `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='推送消息表';