DROP TABLE IF EXISTS `onethink_umeng`;CREATE TABLE `onethink_umeng` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',  `title` varchar(50) DEFAULT NULL,  `appkey` varchar(100) DEFAULT NULL,  `umeng_message_secret` varchar(100) DEFAULT NULL,  `app_master_secret` varchar(100) DEFAULT NULL,  `link` varchar(255) NOT NULL DEFAULT '' COMMENT '链接地址',  `cate_id` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '位置',  `level` int(3) unsigned NOT NULL DEFAULT '50' COMMENT '优先级',  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '状态（0：禁用，1：正常）',  `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='友盟表';DROP TABLE IF EXISTS `onethink_umeng_list`;CREATE TABLE `onethink_umeng_list` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,  `timestamp` varchar(20) DEFAULT NULL,  `ticker` varchar(100) DEFAULT NULL,  `title` varchar(100) DEFAULT NULL,  `text` varchar(200) NOT NULL,  `pctype` int(2) NOT NULL DEFAULT '1' COMMENT '1:android 2:ios 3:all',  `type` varchar(50) DEFAULT NULL COMMENT 'unicast-单播listcast-列播filecast-文件播broadcast-广播groupcast-组播',  `display_type`  varchar(50) DEFAULT NULL COMMENT 'notification-通知，message-消息',  `after_open`  varchar(50) DEFAULT NULL COMMENT 'go_app-打开应用,go_url-跳转到URL,go_activity-打开特定的activity,go_custom-用户自定义内容。',  `url`  varchar(255) DEFAULT NULL COMMENT 'go_url通知栏点击后跳转的URL，要求以http或者https开头',  `activity`  varchar(50) DEFAULT NULL COMMENT 'go_activity通知栏点击后打开的Activity',  `custom`  varchar(200) DEFAULT NULL COMMENT 'display_type=notification且"after_open"为"go_custom"时，该字段必填。用户自定义内容, 可以为字符串或者JSON格式。',  `status` int(2) NOT NULL DEFAULT '1',  `result` int(2) NOT NULL DEFAULT '1',  `level` int(2) NOT NULL DEFAULT '1',  `createtime` int(10) DEFAULT NULL,  `updatetime` int(10) DEFAULT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='友盟消息表';DROP TABLE IF EXISTS `onethink_umeng_user`;CREATE TABLE `onethink_umeng_user` (  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',  `uid` int(10) NOT NULL DEFAULT '0',  `token` varchar(200) DEFAULT NULL,  `channel_id` varchar(200) DEFAULT NULL,  `type` tinyint(1) NOT NULL DEFAULT '1' COMMENT '类型（1：Android，2：iOS）',  `status` tinyint(2) NOT NULL DEFAULT '1' COMMENT '状态（-1：禁用，1：正常）',  `create_time` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '添加时间',  PRIMARY KEY (`id`)) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='友盟用户表';